// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

import '@pancakeswap/pancake-swap-lib/contracts/math/SafeMath.sol';
import '@pancakeswap/pancake-swap-lib/contracts/token/BEP20/IBEP20.sol';
import '@pancakeswap/pancake-swap-lib/contracts/token/BEP20/SafeBEP20.sol';
import '@pancakeswap/pancake-swap-lib/contracts/access/Ownable.sol';

import './PaybVirtualToken.sol';

// Main contract for farming and staking
contract MasterVault is Ownable {
    using SafeMath for uint256;
    using SafeBEP20 for IBEP20;

    // Info of each user.
    struct UserInfo {
        uint256 amount;     // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of PAYBs
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accCakePerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accCakePerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    mapping(address => uint256) public cooldownPeriodEnd;

    // Info of each pool.
    struct PoolInfo {
        IBEP20 lpToken;           // Address of LP token contract.
        uint256 allocPoint;       // How many allocation points assigned to this pool. PAYBs to distribute per block.
        uint256 lastRewardBlock;  // Last block number that PAYBs distribution occurs.
        uint256 accPaybPerShare; // Accumulated PAYBs per share, times 1e12. See below.
    }

    // The PAYB TOKEN!
    IBEP20 public payb;
    // The PaybVirtualToken TOKEN!
    PaybVirtualToken public paybVirtual;
    // PAYB tokens created per block.
    uint256 public paybPerBlock;
    // Muliplier for payb makers.
    uint256 public rewardMultiplier = 1;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Mapping for pool tokens existance
    mapping(address => bool) public poolExistance;
    // Info of each user that stakes LP tokens.
    mapping (uint256 => mapping (address => UserInfo)) public userInfo;
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when PAYB mining starts.
    uint256 public startBlock;

    // Maximal rewardMultiplier for payb makers.
    uint256 public constant MAX_MULTIPLIER = 10;

    // Maximal pool length
    uint256 public constant MAX_POOL_LENGTH = 1024;

    // Maximal unstake time
    uint256 public constant MAX_UNSTAKE_PERIOD = 72 hours;

    // Maximal unstake fee
    uint256 public constant MAX_UNSTAKE_FEE = 100; // 1%

    // Address of master token supplier
    address public masterSupplier;

    // Cooldown period for unstaking
    uint256 public unstakePeriod;

    // Fee for unstaking
    uint256 public unstakeFee;

    // Fee collector
    address public treasury;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event Stake(address indexed user, uint256 indexed pid, uint256 amount);
    event Unstake(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event SupplierUpdate(address indexed supplier);
    event MultiplierUpdate(uint256 multiplier);
    event FeeUpdate(uint256 fee);
    event PeriodUpdate(uint256 period);
    event AddPool(address indexed token, uint256 points);
    event SetPool(uint256 indexed pid, uint256 points);

    constructor(
        IBEP20 _payb,
        PaybVirtualToken _paybVirtual,
        uint256 _paybPerBlock,
        uint256 _startBlock,
        address _treasury
    ) public {
        payb = _payb;
        paybVirtual = _paybVirtual;
        paybPerBlock = _paybPerBlock;
        startBlock = _startBlock;
        unstakePeriod = MAX_UNSTAKE_PERIOD;
        unstakeFee = MAX_UNSTAKE_FEE;
        treasury = _treasury;
        // staking pool
        poolInfo.push(PoolInfo({
            lpToken: _payb,
            allocPoint: 1000,
            lastRewardBlock: startBlock,
            accPaybPerShare: 0
        }));
        totalAllocPoint = 1000;
    }

    modifier supplierExcluded() {
        require(msg.sender != masterSupplier, 'supplier cannot stake here!');
        _;
    }

    function updateMasterSupplier(address newSupplier) external onlyOwner {
        masterSupplier = newSupplier;
        emit SupplierUpdate(masterSupplier);
    }

    function updateMultiplier(uint256 multiplierNumber) external onlyOwner {
        require(multiplierNumber <= MAX_MULTIPLIER, 'Too high multiplier number');
        rewardMultiplier = multiplierNumber;
        emit MultiplierUpdate(rewardMultiplier);
    }

    // Sets unstake period, only callable by owner
    function setUnstakePeriod(uint256 _unstakePeriod) external onlyOwner {
        require(
            _unstakePeriod <= MAX_UNSTAKE_PERIOD,
            'unstakePeriod cannot be more than MAX_UNSTAKE_PERIOD'
        );
        unstakePeriod = _unstakePeriod;
        emit PeriodUpdate(unstakePeriod);
    }

    // Sets unstake fee, only callable by owner
    function setUnstakeFee(uint256 _unstakeFee) external onlyOwner {
        require(
            _unstakeFee <= MAX_UNSTAKE_FEE,
            'unstakeFee cannot be more than MAX_UNSTAKE_FEE'
        );
        unstakeFee = _unstakeFee;
        emit FeeUpdate(unstakeFee);
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // Will not allow to add the same LP token more than once.
    function add(uint256 _allocPoint, IBEP20 _lpToken, bool _withUpdate) external onlyOwner {
        require(poolInfo.length < MAX_POOL_LENGTH, 'Maximal pool length reached');
        if (_withUpdate) {
            massUpdatePools();
        }
        if (poolExistance[address(_lpToken)]) {
            return;
        }
        else {
            poolExistance[address(_lpToken)] = true;
        }

        uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(PoolInfo({
            lpToken: _lpToken,
            allocPoint: _allocPoint,
            lastRewardBlock: lastRewardBlock,
            accPaybPerShare: 0
        }));
        updateStakingPool();
        emit AddPool(address(_lpToken), _allocPoint);
    }

    // Update the given pool's PAYB allocation point. Can only be called by the owner.
    function set(uint256 _pid, uint256 _allocPoint, bool _withUpdate) external onlyOwner {
        require(_pid < poolInfo.length, 'Pool id out of range');
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 prevAllocPoint = poolInfo[_pid].allocPoint;
        poolInfo[_pid].allocPoint = _allocPoint;
        if (prevAllocPoint != _allocPoint) {
            totalAllocPoint = totalAllocPoint.sub(prevAllocPoint).add(_allocPoint);
            updateStakingPool();
        }
        emit SetPool(_pid, _allocPoint);
    }

    // Deposit LP tokens to MasterVault for PAYB allocation.
    function deposit(uint256 _pid, uint256 _amount) external supplierExcluded{
        require (_pid != 0, 'deposit PAYB by staking');
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accPaybPerShare).div(1e12).sub(user.rewardDebt);
            if(pending > 0) {
                safePaybTransfer(msg.sender, pending);
            }
        }
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accPaybPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    // Withdraw LP tokens from MasterVault.
    function withdraw(uint256 _pid, uint256 _amount) external supplierExcluded {
        require (_pid != 0, 'withdraw PAYB by unstaking');
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, 'withdraw: not good');
        updatePool(_pid);
        uint256 pending = user.amount.mul(pool.accPaybPerShare).div(1e12).sub(user.rewardDebt);
        if(pending > 0) {
            safePaybTransfer(msg.sender, pending);
        }
        if(_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accPaybPerShare).div(1e12);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    // Stake PAYB tokens to MasterVault
    function enterStaking(uint256 _amount) external supplierExcluded {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        updatePool(0);
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accPaybPerShare).div(1e12).sub(user.rewardDebt);
            if(pending > 0) {
                safePaybTransfer(msg.sender, pending);
            }
        }
        if(_amount > 0) {
            cooldownPeriodEnd[msg.sender] = block.timestamp.add(unstakePeriod);
            pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accPaybPerShare).div(1e12);

        paybVirtual.mint(msg.sender, _amount);
        emit Stake(msg.sender, 0, _amount);
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Withdraw PAYB tokens from STAKING.
    function leaveStaking(uint256 _amount) external {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        require(user.amount >= _amount, 'withdraw: not good');
        updatePool(0);
        uint256 pending = user.amount.mul(pool.accPaybPerShare).div(1e12).sub(user.rewardDebt);
        if(pending > 0) {
            safePaybTransfer(msg.sender, pending);
        }
        if(_amount > 0) {
            require(cooldownPeriodEnd[msg.sender] <= block.timestamp, 'withdraw: not yet possible');
            uint256 fee = _amount.mul(unstakeFee).div(10000);
            uint256 userReturn = _amount.sub(fee);
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), userReturn);
            pool.lpToken.safeTransfer(address(treasury), fee);
        }
        user.rewardDebt = user.amount.mul(pool.accPaybPerShare).div(1e12);

        paybVirtual.burn(msg.sender, _amount);
        emit Unstake(msg.sender, 0, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) external {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        if(_pid == 0) {
            require(cooldownPeriodEnd[msg.sender] <= block.timestamp, 'withdraw: not yet possible');
        }
        pool.lpToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // Additional time interface for easier integration
    function timeToLeaveStaking(address user) view external returns (uint256){
        return cooldownPeriodEnd[user];
    }

    // View function to see pending PAYBs on frontend.
    function pendingPayb(uint256 _pid, address _user) view external returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accPaybPerShare = pool.accPaybPerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
            uint256 paybReward = multiplier.mul(paybPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accPaybPerShare = accPaybPerShare.add(paybReward.mul(1e12).div(lpSupply));
        }
        return user.amount.mul(accPaybPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Current supplier allowance for Payb 
    function currentSupplierAllowance() view external returns (uint256) {
        return payb.allowance(address(masterSupplier), address(this));
    }

    // Additional return estimation for easier integration
    // Average block time on Binance Smart Chain is 3s
    // Estimated daily return would be calculated over 28 800 blocks
    function getPoolInvervalReturn(uint256 poolNum, uint256 blocks) view external returns (uint256){
        uint256 poolPoints = poolInfo[poolNum].allocPoint;
        uint256 multiplier = getMultiplier(block.number, block.number.add(blocks));
        return multiplier.mul(paybPerBlock).mul(poolPoints).div(totalAllocPoint);
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 paybReward = multiplier.mul(paybPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
        require(payb.allowance(address(masterSupplier), address(this)) >= paybReward, 'not enough payb approved on contract!');
        payb.transferFrom(address(masterSupplier), address(paybVirtual), paybReward);
        pool.accPaybPerShare = pool.accPaybPerShare.add(paybReward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = block.number;
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        return _to.sub(_from).mul(rewardMultiplier);
    }

    function updateStakingPool() internal {
        uint256 length = poolInfo.length;
        uint256 points = 0;
        for (uint256 pid = 1; pid < length; ++pid) {
            points = points.add(poolInfo[pid].allocPoint);
        }
        if (points != 0) {
            points = points.div(3);
            totalAllocPoint = totalAllocPoint.sub(poolInfo[0].allocPoint).add(points);
            poolInfo[0].allocPoint = points;
        }
    }

    // Safe payb transfer function, just in case if rounding error causes pool to not have enough PAYBs.
    function safePaybTransfer(address _to, uint256 _amount) internal {
        paybVirtual.safePaybTransfer(_to, _amount);
    }
}
